package br.com.fiap.strings;

public class Exemplo3StarWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// o metodo starWith verifica se o método começa com uma palavra 
		
		
		String textoD = "Tenha pacirncia marcelo ja vai chgar";
		
		if(textoD.startsWith("Tenha")) {
			System.out.println("O texto("+textoD+") começa com (tenha)");
		}else {
			System.out.println("Não começa");
		}
		
		
		
		
	}

}
