package br.com.fiap.strings;

public class Exemplo4EndWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	String textoD = "Tenha pacirncia marcelo ja vai chgar";
		
		if(textoD.endsWith("gar")) {
			System.out.println("O texto("+textoD+") termina com (gar)");
		}else {
			System.out.println("Não começa");
		}
	}

}
