package br.com.fiap.strings;

public class Exemplo6CharAt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//Esse método recebe como padrão o parametro, ou indice do caracter
		
		
		
		String nomeCompleto = "Paulo roberto de souza";
		
		char terceiroChar = nomeCompleto .charAt(1);
		
		System.out.println("O terceiro caractere da string ("+ nomeCompleto + ") eh: "+ terceiroChar);
	}

}
