package br.com.fiap.strings;

public class Exemplo8LastIndexOf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// tem como função pegar a ultima posição do caracter

		String nomeCompleto = "Paulo Roberto de Souza";
		
		
		System.out.println("A letra 'u' aparece por ultimo na posicao (" + nomeCompleto.lastIndexOf('u')
				+ ") da string (" + nomeCompleto + ")");

	}

}
